mapper.sql:
WHERE RH.ENTTY_NAME = 'GA_USER_ROLE' AND RH.ENTTY_KEY1 = #{userNo}
AND R.ROLE_NAME LIKE CONCAT(#{roleName},'%')
GROUP BY RH.ENTTY_KEY1, RH.ENTTY_KEY2, RH.REASON_NO

WHERE RH.ENTTY_NAME = 'GA_USER_ROLE_GROUP' AND RH.ENTTY_KEY1 = #{userNo}
AND RG.ROLE_GROUP_NAME LIKE CONCAT(#{roleName},'%')
GROUP BY RH.ENTTY_KEY1, RH.ENTTY_KEY2, RH.REASON_NO
