sql.where:
AND I.DATA_STATE = 'A'

	AND I.ATHRT_ITEM_TYPE IN

		#{item}



	AND I.SYSTEM_CODE IN

		#{item}



	AND I.ITEM_NO IN

		#{item}



	AND I.ITEM_NO = #{itemNo}


	AND I.MENU_NAME LIKE CONCAT('%', #{menuName}, '%')


	AND I.ITEM_NAME LIKE CONCAT('%', #{itemName}, '%')


	AND I.FNCTN_ID LIKE CONCAT('%', #{fnctnId}, '%')


	AND I.SYSTEM_APLCT_YN = #{systemAplctYn}


	AND I.USE_YN = #{useYn}


	AND I.ITEM_NAME LIKE CONCAT('%', #{searchInput}, '%') OR I.ITEM_DSCRP LIKE CONCAT('%', #{searchInput}, '%')


	AND I.ITEM_DSCRP LIKE CONCAT('%', #{itemDscrp}, '%')



		AND I.MOD_DATE >= STR_TO_DATE(#{startDate},'%Y%m%d')
		AND I.MOD_DATE <= STR_TO_DATE(CONCAT(#{endDate}, '235959'),'%Y%m%d%H%i%s')


		AND I.REG_DATE >= STR_TO_DATE(#{startDate},'%Y%m%d')
		AND I.REG_DATE <= STR_TO_DATE(CONCAT(#{endDate}, '235959'),'%Y%m%d%H%i%s')

mapper.insert:
/* kr.fingate.gs.auth.item.dao.ItemDao.insItem */
INSERT INTO GA_ITEM (
	  SYSTEM_CODE
	, ATHRT_ITEM_TYPE
	, ITEM_DSCRP
	, MENU_NAME
	, ITEM_NAME
	, FNCTN_ID
	, SYSTEM_APLCT_YN
	, USE_YN
	, REG_USER_NO
) VALUES(
	 #{systemCode}
	, #{authItemType}
	, #{itemDscrp}
	, #{menuName}
	, #{itemName}
	, #{fnctnId}
	, #{systemAplctYn}
	, #{useYn}
	, #{rgstUserNo}
)

mapper.update:
 		/* kr.fingate.gs.auth.item.dao.ItemDao.updItem */
		UPDATE GA_ITEM
		SET
			MOD_DATE = NOW()
		  , MOD_USER_NO = #{modUserNo}

			 , ITEM_DSCRP = #{itemDscrp}


			 , SYSTEM_APLCT_YN = #{systemAplctYn}


			 , USE_YN = #{useYn}

		WHERE
			 ITEM_NO = #{itemNo}

mapper.select:
/* kr.fingate.gs.auth.item.dao.ItemDao.getFnctnIdNum */
SELECT
	 MAX(I.FNCTN_ID) AS FNCTN_ID
FROM
	 GA_ITEM I
WHERE
	 I.SYSTEM_CODE = #{systemCode}
	 AND I.ATHRT_ITEM_TYPE = #{authItemType}
