mapper.select:
/* SsoLoginDao.getLogin */
SELECT CLIENT_NO
     , LOGIN_EMAIL
     , LOGIN_PSWRD
FROM FG_USER
WHERE CLIENT_NO = #{clientNo}
  AND LOGIN_EMAIL = #{loginEmail}
  AND DATA_STATUS = 'A'

/* SsoLoginDao.getToken */
SELECT CLIENT_NO
     , USER_NO
     , USER_NAME
FROM FG_USER
WHERE CLIENT_NO = #{clientNo}
AND LOGIN_EMAIL = #{loginEmail}
AND DATA_STATUS = 'A'
