mapper.insert:
/* kr.fingate.gs.auth.auth.dao.RoleModDao.insRole */
INSERT INTO GA_ROLE (
          CLIENT_NO
        , ROLE_NAME
        , ROLE_DSCRP
        , USE_YN
        , DATA_STATE
        , REG_DATE
        , REG_USER_NO
        , MOD_DATE
        , MOD_USER_NO
)
VALUES (
         #{clientNo}
       , #{roleName}
       , #{roleDscrp}
       , #{useYn}
       , 'A'
       , NOW()
       , #{regUserNo}
       , NOW()
       , #{modUserNo}
       )

mapper.update:
/* kr.fingate.gs.auth.auth.dao.RoleModDao.updRole */
UPDATE GA_ROLE
   SET MOD_DATE = NOW()
     , MOD_USER_NO = #{modUserNo}

    , ROLE_NAME = #{roleName}


     , ROLE_DSCRP = #{roleDscrp}


     , USE_YN = #{useYn}


     , DATA_STATE = #{dataState}

 WHERE ROLE_NO = #{roleNo}

/* kr.fingate.gs.auth.auth.dao.RoleModDao.updRoleItem */ INSERT INTO GA_ROLE_ITEM_MAP ( ROLE_NO , ITEM_NO , DATA_STATE , REG_DATE , REG_USER_NO , MOD_DATE , MOD_USER_NO ) VALUES
(#{item.roleNo}, #{item.itemNo}, #{item.dataState}, NOW(), #{regUserNo}, NOW(), #{modUserNo})
ON DUPLICATE KEY UPDATE MOD_DATE = NOW() , MOD_USER_NO = #{modUserNo} , DATA_STATE = values(DATA_STATE)

mapper.insert:
