       , #{roleGroupDscrp}
       , #{useYn}
       , 'A'
       , NOW()
       , #{regUserNo}
       , NOW()
       , #{modUserNo}
       )

mapper.update:
/* kr.fingate.gs.auth.auth.dao.RoleModDao.updRoleGroup */
UPDATE GA_ROLE_GROUP
SET MOD_DATE = NOW()
, MOD_USER_NO = #{modUserNo}

    , ROLE_GROUP_NAME = #{roleGroupName}


    , ROLE_GROUP_DSCRP = #{roleGroupDscrp}


    , USE_YN = #{useYn}


    , DATA_STATE = #{dataState}

WHERE ROLE_GROUP_NO = #{roleGroupNo}

/* kr.fingate.gs.auth.auth.dao.RoleModDao.updRollGroupMap */ INSERT INTO GA_ROLE_GROUP_MAP ( ROLE_GROUP_NO , ROLE_NO , DATA_STATE , REG_DATE , REG_USER_NO , MOD_DATE , MOD_USER_NO ) VALUES
(#{item.roleGroupNo}, #{item.roleNo}, #{item.dataState}, NOW(), #{regUserNo}, NOW(), #{modUserNo})
ON DUPLICATE KEY UPDATE MOD_DATE = NOW() , MOD_USER_NO = #{modUserNo} , DATA_STATE = values(DATA_STATE)
