mapper.select:
/* kr.fingate.gs.sales.cstmr.dao.CstmrDao.getDuplicateCstmr */
SELECT CS.CLIENT_NO, CS.CSTMR_NO, CS.CSTMR_NAME, CS.RSPNS_USER_NO, CS.CLPHN_NO, DATE_FORMAT(CS.REG_DATE, '%Y%m%d%H%i%S' ) AS REG_DATE
FROM TB_CSTMR CS
WHERE CS.DATA_STATE = 'A'
  AND CS.CLIENT_NO = #{clientNo}
  AND CS.RSPNS_USER_NO = #{rspnsUserNo}


          AND CS.CLPHN_NO = #{endClphnNo}


          AND CS.IDNTN_NO = #{encIdntnNo}


          AND CS.IDNTN_NO = #{encIdntnNo}


ORDER BY CS.REG_DATE DESC
LIMIT 1

/* kr.fingate.gs.sales.cstmr.dao.CstmrDao.getCstmrSmsAuth */ SELECT CSSA.SMS_AGRMN_SEQ , CSSA.AUTH_NO , CSSA.RCV_TLPHN_NO , CSTMR_NO , CSSA.SEND_TLPHN_NO , SEND_USER_NO , MSG , AUTH_YN , AUTH_PRFRM_USER , DATE_FORMAT(AUTH_DT, '%Y%m%d%H%i%S' ) AS AUTH_DT , DATE_FORMAT(REG_DATE, '%Y%m%d%H%i%S' ) AS REG_DATE FROM TB_CSTMR_SMS_AUTH CSSA
AND CSTMR_NO = #{cstmrNo}
AND SMS_AGRMN_SEQ = #{smsAgrmnSeq}
AND AUTH_NO = #{authNo}
ORDER BY SMS_AGRMN_SEQ DESC LIMIT 1

mapper.sql:
SELECT CSRC.CHANGE_SEQ , CSRC.CSTMR_NO , CSRC.EXSTN_RSPNS_USER_NO , U1.USER_NAME AS EXSTN_RSPNS_USER_NAME , U1.ORGNZ_NO AS EXSTN_RSPNS_ORGNZ_NO , U1.ORGNZ_NAME AS EXSTN_RSPNS_ORGNZ_NAME , CSRC.CRNT_RSPNS_USER_NO , U2.USER_NAME AS CRNT_RSPNS_USER_NAME , U2.ORGNZ_NO AS CRNT_RSPNS_ORGNZ_NO , U2.ORGNZ_NAME AS CRNT_RSPNS_ORGNZ_NAME , CSRC.CHANGE_REASON , CSRC.CHANGE_TYPE , CSRC.REG_USER_NO , U2.USER_NAME AS REG_USER_NAME , U2.ORGNZ_NO AS REG_ORGNZ_NO , U2.ORGNZ_NAME AS REG_ORGNZ_NAME , DATE_FORMAT(CSRC.REG_DATE, '%Y%m%d%H%i%S') AS REG_DATE FROM TB_CSTMR_RSPNS_CHANGE CSRC JOIN TB_USER U1 ON CSRC.EXSTN_RSPNS_USER_NO = U1.USER_NO JOIN TB_USER U2 ON CSRC.CRNT_RSPNS_USER_NO = U2.USER_NO JOIN TB_USER U3 ON CSRC.REG_USER_NO = U3.USER_NO
AND CSRC.CSTMR_NO = #{cstmrNo}
AND CSRC.PRCSN_STATE = #{prcsnState}
AND CSRC.CHANGE_SEQ = #{changeSeq}
